
let imageBase64 = 'data:image/jpeg;base64,';

function imprimirHC(idTratamiento) {

    var entidadHistoriaClinica = traerDatosHC(idTratamiento);
    var docDefinition = armarDocumentoHC(entidadHistoriaClinica);
    var docDefinition2 = armarDocumentoConsentimiento(entidadHistoriaClinica);

    var fecha = moment().format("DDMMyyyy");
    //DEFINIR NOMBRE DE LOS ARCHIVOS
    var docDefinitionName = fecha + '_' + entidadHistoriaClinica.paciente + '_ANTECEDENTES.pdf';
    var docDefinitionName2 = fecha + '_' + entidadHistoriaClinica.paciente + '_CONSENTIMIENTO.pdf';

    pdfMake.createPdf(docDefinition).download(docDefinitionName);
    pdfMake.createPdf(docDefinition2).download(docDefinitionName2);
}
function armarDocumentoConsentimiento(entidadHistoriaClinica) {

    moment.lang('es', {
        months: 'enero_febrero_marzo_abril_mayo_junio_julio_agosto_septiembre_octubre_noviembre_diciembre'.split('_'),
        monthsShort: 'ene._feb._mar_abr._may_jun_jul._ago_sept._oct._nov._dec.'.split('_'),
        weekdays: 'domingo_lunes_martes_miercoles_jueves_viernes_sabado'.split('_'),
        weekdaysShort: 'dom._lun._mar._mier._jue._vier._sab.'.split('_'),
        weekdaysMin: 'Do_Lu_Ma_Mi_Ju_Vi_Sa'.split('_')
    });

    var momentDay = moment();
    var fechaFormatoLargo = momentDay.locale('es').format('dddd DD') + ' de ' + momentDay.locale('es').format('MMMM') + ' del año ' + momentDay.locale('es').format('YYYY');

    var espacio = "   ";
    var textMargenH = 5;
    var textMargenV = 2;

    return docDefinition = {
        pageSize: 'A4',
        pageOrientation: 'portrait',
        pageMargins: [40, 45, 40, 45],

        content: [
            {
                layout: 'lightHorizontalLines',
                table: {
                    headerRows: 1,
                    widths: ['10%', '80%', '10%'],
                    body: [
                        [{ image: imageBase64, fit: [50, 50] },
                        {
                            text: 'CONSENTIMIENTO INFORMADO', style: 'header'
                        }, { text: '' }
                        ]
                    ]
                }
            },
            {
                text:
                    [
                        {
                            lineHeight: 2.5,
                            text: 'El día ' + fechaFormatoLargo + ' el Licenciado ' + entidadHistoriaClinica.profesional + ' (Mat.: ' + entidadHistoriaClinica.profesionalMatricula
                                + ') me ha explicado en mi lengua natal y en términos sencillos que he entendido en su totalidad la afección que padezco '
                                + 'y lo riesgos que puedo correr en el futuro. También me ha explicado y yo he entendido el tratamiento al que seré sometido para lo cual '
                                + 'presto mi total consentimiento. Igualmente, se me ha explicado detalladamente y he comprendido que el tratamiento al cual habré de someterme '
                                + 'presenta ciertos inconvenientes y riesgos.',
                            style: 'textStyle'
                        }

                    ],
                margin: [30, 30, 30, 5]
            },
            {
                text:
                    [
                        {
                            lineHeight: 2.5,
                            text: 'Asimismo, se me informó que además del tratamiento sugerido, existen otras alternativas que poseen resultados aceptables para la afección '
                                + 'que presento y las razones por las que no han sido indicadas. Por tal motivo, acepto la indicación que se me ha realizado y por lo tanto '
                                + 'autorizo al Licenciado ' + entidadHistoriaClinica.profesional + ' (Mat.: ' + entidadHistoriaClinica.profesionalMatricula
                                + ') para que realice el tratamiento tal cual me fue informado.',
                            style: 'textStyle'
                        }
                    ],
                margin: [30, 5, 30, 5]
            },
            {
                layout: 'noBorders',
                table: {
                    headerRows: 1,
                    widths: ['50%', '50%'],
                    body: [
                        [{ text: '______________________', bold: true, alignment: 'left' }, { text: '______________________', bold: true, alignment: 'right' }],
                        [{ text: 'Firma del Paciente', bold: true, alignment: 'left' }, { text: 'Firma del Profesional', bold: true, alignment: 'right' }],
                        [{ text: entidadHistoriaClinica.paciente, bold: false, alignment: 'left' }, { text: entidadHistoriaClinica.profesional, bold: false, alignment: 'right' }],
                        [{ text: 'D.N.I.: ' + entidadHistoriaClinica.documento, bold: false, alignment: 'left' }, { text: 'D.N.I.: ' + entidadHistoriaClinica.profesionalDocumento + ', Mat.: ' + entidadHistoriaClinica.profesionalMatricula, bold: false, alignment: 'right' }]
                    ]
                },
                margin: [textMargenH, 45, textMargenH, textMargenV]
            }
        ],

        styles: {
            header: {
                fontSize: 20,
                bold: true,
                alignment: 'center',
                decoration: 'underline',
                margin: [10, 10]
            },
            subtitle: {
                fontSize: 13,
                bold: true,
                alignment: 'left',
                decoration: 'underline',
                margin: [textMargenH, 8]
            },
            textHeader: {
                fontSize: 13,
                bold: true,
                alignment: 'left'
            },
            textBold: {
                fontSize: 13,
                bold: true,
                alignment: 'left',
                margin: [textMargenH, 0]

            },
            textStyle: {
                fontSize: 13,
                bold: false,
                alignment: 'left',
                margin: [textMargenH, 0]

            }
        }
    }
}
