function traerDatosHC(idTratamiento) {

    var entidadHistoriaClinica;
    $.ajax({
        url: "GestionSeguimientoPacientes.aspx/traerDatosHCImprimir",
        data: "{idTratamiento: '" + idTratamiento + "'}",
        type: "post",
        contentType: "application/json",
        async: false,
        success: function (data) {

            var valores = JSON.parse(data.d);
            debugger;
            var profesional = valores[0]['PROFESIONAL'] == null ? 'Sin datos.' : valores[0]['PROFESIONAL'];
            var profesionalMatricula = valores[0]['MATRICULA_PROFESIONAL'] == null ? '' : valores[0]['MATRICULA_PROFESIONAL'];
            var profesionalDocumento = valores[0]['DOCUMENTO_PROFESIONAL'] == null ? '' : valores[0]['DOCUMENTO_PROFESIONAL'];

            var paciente = valores[0]['APELLIDO'] + ' ' + valores[0]['NOMBRE'];
            var obraSocial = valores[0]['OBRA_SOCIAL'] == null ? 'Sin datos.' : valores[0]['OBRA_SOCIAL'];
            var carnet = valores[0]['NRO_AFILIADO'] == null ? 'Sin datos.' : valores[0]['NRO_AFILIADO'];
            var domicilio = valores[0]['DOMICILIO'] == null ? 'Sin datos.' : valores[0]['DOMICILIO'];
            var localidad = valores[0]['LOCALIDAD'] == null ? 'Sin datos.' : valores[0]['LOCALIDAD'];
            var telefono = valores[0]['NRO_CONTACTO'] == null ? 'Sin datos.' : valores[0]['NRO_CONTACTO'];
            var nacimiento = valores[0]['FECHA_NACIMIENTO'] == null ? 'Sin datos.' : valores[0]['FECHA_NACIMIENTO'];
            var documento = valores[0]['DOCUMENTO'] == null ? 'Sin datos.' : valores[0]['DOCUMENTO'];
            var profDerivante = valores[0]['PROFESIONAL_DERIVANTE'] == null ? 'Sin datos.' : valores[0]['PROFESIONAL_DERIVANTE'];
            var matDerivante = valores[0]['MATRICULA_DERIVANTE'] == null ? 'Sin datos.' : valores[0]['MATRICULA_DERIVANTE'];
            var especDerivante = valores[0]['ESPECIALIDAD_DERIVANTE'] == null ? 'Sin datos.' : valores[0]['ESPECIALIDAD_DERIVANTE'];
            var antecedentesFlia = valores[0]['ANTECEDENTES'] == null ? 'Sin datos.' : valores[0]['ANTECEDENTES'];
            var diagnosticoIngreso = valores[0]['DIAGNOSTICO_INGRESO'] == null ? 'Sin datos.' : valores[0]['DIAGNOSTICO_INGRESO'];
            var evaIngreso = valores[0]['EVALUACION_INGRESO'] == null ? 'Sin datos.' : valores[0]['EVALUACION_INGRESO'];
            var planTratamiento = valores[0]['DESCRIPCION_PLAN'] == null ? 'Sin datos.' : valores[0]['DESCRIPCION_PLAN'];
            var objPlan = valores[0]['OBJETIVO_PLAN'] == null ? 'Sin datos.' : valores[0]['OBJETIVO_PLAN'];

            var tension = valores[0]['TENSION'] == true ? 'Sí' : 'No';
            var tensionValores = valores[0]['TENSION_VALORES'] == null ? '--/--' : valores[0]['TENSION_VALORES'];
            var diabetes = valores[0]['DIABETES'] == true ? 'Sí' : 'No';
            var fumador = valores[0]['FUMADOR'] == true ? 'Sí' : 'No';
            var cardiaco = valores[0]['CARDIACO'] == true ? 'Sí' : 'No';
            var cirrosis = valores[0]['CIRROSIS'] == true ? 'Sí' : 'No';
            var artrosis = valores[0]['ARTROSIS'] == true ? 'Sí' : 'No';
            var artritis = valores[0]['ARTRITIS_REUMATOIDEA'] == true ? 'Sí' : 'No';
            var hemiplejia = valores[0]['HEMIPLEJIA'] == true ? 'Sí' : 'No';
            var asma = valores[0]['ASMA'] == true ? 'Sí' : 'No';
            var marcapasos = valores[0]['MARCAPASOS'] == true ? 'Sí' : 'No';
            var protesis = valores[0]['PROTESIS'] == true ? 'Sí' : 'No';
            var caderaDerecha = valores[0]['CADERA_DERECHA'] == true ? 'Sí' : 'No';
            var caderaIzquierda = valores[0]['CADERA_IZQUIERDA'] == true ? 'Sí' : 'No';
            var otros = valores[0]['OTROS'] == null ? 'Sin datos.' : valores[0]['OTROS'];

            entidadHistoriaClinica = {
                profesional: profesional,
                profesionalMatricula: profesionalMatricula,
                profesionalDocumento: profesionalDocumento,
                paciente: paciente,
                obraSocial: obraSocial,
                carnet: carnet,
                domicilio: domicilio,
                localidad: localidad,
                telefono: telefono,
                nacimiento: nacimiento,
                documento: documento,
                profDerivante: profDerivante,
                matDerivante: matDerivante,
                especDerivante: especDerivante,

                antecedentesFlia: antecedentesFlia,
                diagnosticoIngreso: diagnosticoIngreso,
                evaIngreso: evaIngreso,
                planTratamiento: planTratamiento,
                objPlan: objPlan,

                tension: tension,
                tensionValores: tensionValores,
                diabetes: diabetes,
                fumador: fumador,
                cardiaco: cardiaco,
                cirrosis: cirrosis,
                artrosis: artrosis,
                artritis: artritis,
                hemiplejia: hemiplejia,
                asma: asma,
                marcapasos: marcapasos,
                protesis: protesis,
                caderaDerecha: caderaDerecha,
                caderaIzquierda: caderaIzquierda,
                otros: otros

            }

        },
        error: function (xhr, ajaxOptions, thrownError) {
            swal("Cuidado", "Ha habido un error al generar el Archivo.", "warning");
        }
    });
    return entidadHistoriaClinica;
}
